import React from 'react'
import { Navbar } from '../components/Navbar/Navbar'
import './GenerateQuestion.css'
import axios from 'axios'
import fileDownload from 'js-file-download'
import FileSaver from 'file-saver';
export function GenerateQuestions() {
  function req() {
    const msg = document.getElementById("input").value;
    const request = {
      method: 'POST',
      responseType: 'arraybuffer',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(msg)
  };
      fetch('http://127.0.0.1:5000/get-file/timepass.xlsx ', request)
      .then((response) => {
        var blob = new Blob([response.data], { type: 'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet' });
       // fileDownload(response.data,"timepass.xlsx")
       FileSaver.saveAs(blob, 'timepass.xlsx');
      })


    console.log(JSON.stringify({msg}));
  }
  function req1() {
    const msg = document.getElementById("input").value;
  axios.post('http://127.0.0.1:5000/get-file/timepass.xlsx', { responseType: 'arraybuffer',headers: { 'Content-Type': 'application/json' },  body: msg})
    .then((response) => {
      var blob = new Blob([response.data], { type: 'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet' });
      FileSaver.saveAs(blob, 'timepass.xlsx');
    });


    console.log(msg);
  }
  return (
    <div className='wrapper'>
      <Navbar />
        <section className='content' id="para">
          <div className='content__input'>
            <label>
                <textarea name="text__inp" id="input" cols="80" rows="15" placeholder='Input Paragraph' className='text__inp'></textarea>
            </label>
          </div>
          <button className='generate__btn' onClick={() => req1()}>Generate</button>
        </section>
    </div>
  )
}
